import './login.css'
import { NavLink } from "react-router-dom";
export const Login = ()=>{

    const handleLogin = async () => {
        try {
            const response = await fetch('https://vaika-production.up.railway.app/auth', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({
                    "username": user.email,
                    "password": user.password
                }),
            });
            if (response.ok) {
                const data = await response.json();
                const token = data.token;

                if (token == null){
                    history.push("/")
                    console.log(data)
                } else {
                    localStorage.setItem('jwtToken', token);
                    console.log(data)
                    history.push('/home');
                }
            } else {
                console.error('Erreur d\'authentification');
            }
        } catch (error) {
            console.error('Erreur réseau', error);
        }
    };
return (
          <div className='container mycomposant'>
              <div className="row log">
                    
                        <div className="col-lg-4"></div>
                        <div className="col-lg-4">
                        <h1 className='text-center fw-normal mb-3 mt-3'>Login</h1>
                                <div className="input-group mb-3 mt-5">
                                    
                                        <span className="input-group-text" id="basic-addon1">@</span>
                                        <input type="text" className="form-control" value="tsiorydrage@gmail.com" placeholder="mail" aria-label="Username" aria-describedby="basic-addon1"/>
                                </div>
                                <div className="input-group mb-3 mt-5">
                                    <span className="input-group-text" id="basic-addon1">Password</span>
                                    <input type="password" className="form-control" value="123" placeholder="password" aria-label="Username" aria-describedby="basic-addon1"/>
                                </div> 
                               
                                <button type='submit' onClick={handleLogin}></button>
                                    
                                                         
                        </div>
                        <div className="col-lg-4"></div>
              </div>
          </div>
      );




}
