import axios from 'axios';
import React, { useEffect, useState } from 'react';
import { Navy } from "../nav/Navy";
import { Link,useParams, useNavigate } from "react-router-dom";

type Category = {
  idMarque: string;
    nom: string;
  };
export const AllMarque = ()=>{
  let navigate = useNavigate() ;
  const [cats,setCats] = useState<Category[]>([]);
  useEffect(()=>{
      loadCat();
  },[]);
  const loadCat = async () => {
    try {
      const jwtToken = localStorage.getItem('jwtToken');
      if (!jwtToken) {
        console.error('Jetons JWT non trouvés');
        // Vous pouvez gérer la redirection vers la page de connexion ici
        return;
      }

      const config = {
        headers: {
          Authorization: `Bearer ${jwtToken}`,
        },
      };
        const result = await axios.get("https://vaika-production.up.railway.app/marques",config);
        setCats(result.data);
    } catch (error) {
        console.error("Erreur lors du chargement des catégories :", error);
    }
};
// delete
const { id} = useParams<{ id: string }>();
const deleteCat= async(idCategorie:string)=>{
  await axios.delete(`https://vaika-production.up.railway.app/marque/${idCategorie}`)
  loadCat();
  navigate('/listeM');
}
  return(
    <div>
      <Navy></Navy>
      <div className='container'>
    <div className='py-4 '>
      <table className="table border shadow">
        <thead>
          <tr>
            <th scope="col">#</th>
            <th scope="col">Nom</th>
            <th scope="col">action</th>
          </tr>
        </thead>
        <tbody>
        {cats.map((cat) => (
          <tr key={cat.idMarque}>
              <th scope="row">{cat.idMarque}</th>
              <td>{cat.nom}</td>
              <td>
                  <Link 
                      className='btn btn-outline-primary mx-2' 
                      to={`/editMarque/${cat.idMarque}`}
                  >
                      Edit
                  </Link>
              </td>
              <td>
                  <Link 
                      className='btn btn-outline-danger mx-2' 
                      to={`/deleteCat/${cat.idMarque}`}
                      onClick={()=>deleteCat(cat.idMarque)}
                  >
                  Delete
                  </Link>
              </td>
          </tr>
      ))}
        </tbody>
      </table>
    </div>
  </div>
    </div>





  );

}
