import axios from 'axios';
import React, { useEffect, useState } from 'react';
import { Navy } from "../nav/Navy";
import { Link,useParams, useNavigate } from "react-router-dom";

type Category = {
  idCategorie: string;
    nom: string;
  };
export const AllCat = ()=>{
  let navigate = useNavigate() ;
    const [cats,setCats] = useState<Category[]>([]);
    useEffect(()=>{
        loadCat();
    },[]);
    const loadCat = async () => {
      try {
          const result = await axios.get("http://localhost:8085/categories");
          setCats(result.data);
      } catch (error) {
          console.error("Erreur lors du chargement des catégories :", error);
      }
  };
  // delete
  const { id} = useParams<{ id: string }>();
  const deleteCat= async(idCategorie:string)=>{
    await axios.delete(`http://localhost:8085/categorie/${idCategorie}`)
    loadCat();
    navigate('/listeC');
}
    return(
      <div>
        <Navy></Navy>
        <div className='container'>
      <div className='py-4 '>
        <table className="table border shadow">
          <thead>
            <tr>
              <th scope="col">#</th>
              <th scope="col">Nom</th>
              <th scope="col">action</th>
            </tr>
          </thead>
          <tbody>
          {cats.map((cat) => (
            <tr key={cat.idCategorie}>
                <th scope="row">{cat.idCategorie}</th>
                <td>{cat.nom}</td>
                <td>
                    <Link 
                        className='btn btn-outline-primary mx-2' 
                        to={`/editCat/${cat.idCategorie}`}
                    >
                        Edit
                    </Link>
                </td>
                <td>
                    <Link 
                        className='btn btn-outline-danger mx-2' 
                        to={`/deleteCat/${cat.idCategorie}`}
                        onClick={()=>deleteCat(cat.idCategorie)}
                    >
                    Delete
                    </Link>
                </td>
            </tr>
        ))}
          </tbody>
        </table>
      </div>
    </div>
      </div>





    );
}
